mapper.update:
UPDATE BOARD SET CONTENTS = #{contents}, SUBJECT =
#{subject} WHERE
BOARD_SEQ = #{board_seq}

mapper.delete:
DELETE FROM BOARD_LIKE
WHERE BOARD_SEQ = #{seq} and
USER_ID = #{user_id}

mapper.update:
UPDATE BOARD SET COMMENT_COUNT = COMMENT_COUNT + 1
WHERE BOARD_SEQ =
${value}

UPDATE BOARD SET COMMENT_COUNT = COMMENT_COUNT - 1
WHERE BOARD_SEQ =
${value}

mapper.select:
SELECT
COUNT(*) FROM BOARD_LIKE WHERE BOARD_SEQ = #{seq} AND USER_ID =
#{user_id};
